<?phpnamespace Bfg\Scaffold\LevyPipes\LevyHasOneModel;use Bfg\Scaffold\LevyModel\LevyFieldModel;use Bfg\Scaffold\LevyModel\LevyModel;use Bfg\Scaffold\LevyModel\LevyRelatedTypeModel;use Bfg\Scaffold\LevyModel\LevyRelation\LevyHasOneModel;use Bfg\Scaffold\LevyModel\LevyRelation\LevyRelationAbstract;use Bfg\Scaffold\LevyPipes\ExplainRelationPipeAbstract;use Closure;/** * Class ExplainRelationPipe * @package Bfg\Scaffold\LevyPipes\LevyHasManyModel */class ExplainRelationPipe extends ExplainRelationPipeAbstract{    /**     * @param  LevyRelationAbstract|LevyHasOneModel|mixed  $model     * @param  Closure  $next     * @return mixed     * @throws \Exception     */    public function handle(LevyRelationAbstract $model, Closure $next): mixed    {        $this->related($model,            function (LevyRelatedTypeModel $type, LevyModel $related, LevyModel $parent) use ($model) {                $model->related_table = $related->table;                $model->foreign_key = $type->params[0] ?? $parent->foreign;                $model->local_key = $type->params[1] ?? $related->inherited_field;                $model->relation_params = [                    $model->related."::class",                    $model->foreign_key,                    $model->local_key                ];                if ($model->local_key !== $parent->foreign) {                    $parent->fields->push(                        LevyFieldModel::model($model->local_key, [                            'parent' => $parent, 'parse' => [                                $model->local_key, 'foreignId', $this->makeForeignDefaultParams($type, $related->table)                            ]                        ])                    );                }            });        return $next($model);    }}